package controller;import entity.Board;import entity.GameState;import entity.Player;import entity.TicTacToe;import java.util.List;public class TicTacToeController {    public TicTacToe initializeTheGame(List<Player> players, Board board) {        return TicTacToe.getBuilder()                .setBoard(board)                .setMatchState(GameState.IN_PROGRESS)                .setPlayers(players)                .build();    }    public void printTheBoard(TicTacToe ticTacToe) {        ticTacToe.getBoard().printTheBoard();    }    public GameState getGameState(TicTacToe ticTacToe) {        return ticTacToe.getGameState();    }    public void fillTheBoard(TicTacToe ticTacToe, int row, int col, Player player) {        ticTacToe.makeMove(row, col, player);    }}